<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Home extends CI_Controller {	public $arrMenu = array();	public $data;	public $privilege = array();	public function __construct()	{		parent::__construct();                if(! $_SESSION)                {                    session_start();                }            date_default_timezone_set('UTC');              $this->load->model(array('backend/Model_menu_frontend','web/Model_label','web/Model_content','web/Model_category_frontend'));            $this->load->helper(array('funcglobal','menu','accessprivilege','alias','text'));            $this->data["module_id"]='';            $this->data["menu_id"]=0;                            $categoryparent               = 0;            $this->data['categoryparent'] = $categoryparent;            $cond                     = "WHERE a.module_id = 115 and a.category_active_status = 1 AND a.category_parent = 0 ";            $ListParent               = $this->Model_category_frontend->getListCategory($cond);                $this->data["countParent"] = count($ListParent);            $this->data["ListParent"] = $ListParent;            $this->data["module_banner"]=103;              $this->data["module_testy"]=122;             $string =$this->data["module_banner"].','.$this->data["module_testy"];            $arrayin=array_map('intval', explode(',', $string));            $this->data["where_in"] = implode(",",$arrayin);            $this->data['now'] = date("Y-m-d");	}        	public function index()	{            $ListBanner =array();            $ListTesty =array();                                 $order_limit='';                $order_limit .= " order by a.row_order ASC";                $order_blog ='';                $order_blog .= " order by a.row_order ASC limit 0,4";                $whereContent = '';                $label_page =1;                $whereContent .= " WHERE a.row_active_status=1 and a.row_active_status=1 and a.row_parent=0 and a.module_id in(".$this->data["where_in"].") ";                               $ListContent = $this->Model_content->getListContent($whereContent,$order_limit,$label_page);                                foreach ($ListContent as $lc){                    if ($lc['module_id']== $this->data["module_banner"]){                        $ListBanner[]=$lc;                     }                     else if ($lc['module_id']== $this->data["module_testy"]){                      $ListTesty[]=$lc;                      }                                    }                $this->data["countBanner"] = count($ListBanner);                $this->data["ListBanner"] = $ListBanner;                $this->data["countTesty"] = count($ListTesty);		$this->data["ListTesty"] = $ListTesty;                                $whereBlog ='';                $whereBlog .= " WHERE a.row_active_status=1 and  a.row_parent=0 and a.module_id = 102 and a.publish_date <= '". $this->data['now']."' and a.close_date >= '". $this->data['now']."' ";                              $ListBlog = $this->Model_content->getListContent($whereBlog,$order_blog,$label_page);                                $this->data["countBlog"] = count($ListBlog);		$this->data["ListBlog"] = $ListBlog;//                echo '<pre>';//                print_r($ListContent);//                die;            $this->load->view('vhome',$this->data);	}	}