<?php$session_login = isset($_SESSION['user_data']) ? $_SESSION['user_data'] : '';if ($session_login != "") {    $pecah                    = explode("|", $session_login);    $this->data["agent_id"]   = $pecah[0];    $this->data["first_name"] = $pecah[1];    $this->data["email"]      = $pecah[2];} else {    $this->data["agent_id"]   = '';    $this->data["first_name"] = '';    $this->data["email"]      = '';}